ja.chordwiki.org.css:
/* コード */
span.chord {
  font-family:  MNoto Sans alpha V2;
  font-size: 16px;
  color:#3273cd
}

body {
  line-height: 1.0rem;
  font-family: inherit;
  font-size: 16px;
}

/* 強調 */
div p strong {
  background-color:#e6e6e6;
  padding: 0 6px;
  margin: 10px 0;
  font-size: 15px;
  color: #4a4a4a;
}

p.line {
  padding-top: 1rem;
  padding-bottom: 0.8rem;
  margin-top: 1rem;
  margin-bottom: 0rem;
  font-size: 16px;
}

/* コメント */
p.line.comment {
  width: fit-content;
  margin: 0 0;
  padding: 0 0; 
}

/* キー表示 */
p.key {
  background-color: #feecf0;
  display: inline-block;
  font-size: 16px;
  margin:8px 0;
  padding: 0 6px;
}

/* 評価とか（要らんので消す） */
div.ratestar {
  display: none;
}

div.itunes_side {
  display: none;
}

div.amazon_side {
  display: none;
}

div.extra_side {
  display: none;
}

/* 右のサイドコンテンツ */
#side {
  float: right;
  padding-top: 5rem;
  position: sticky;
}

/* キーとスピード調整 */
#key {
  width: fit-content;
  font-size: 17px;
  float: right;
  margin-right: -10rem;
  position: sticky;
}

#key strong {
  display: none;
}

div form select {
  font-size: inherit;
}

div.selectize-control {
  width: 250px;
}

/* ボタン */
body div button {
  font-size: inherit;
}

/* タイトル */
h1.title {
  text-align: left;
  font-size: 24px;
  border-style: none;
  padding-top: 0px;
}

/* サブタイトル */
h2.subtitle {
  text-align: left;  
  font-size: 14px;
  margin-top: 0px;
  margin-bottom: 1rem;
}

/* 全体の配置 */
div.main {
  padding:0rem 0rem 0rem 6rem
}

footer.footer {
  display: none;
}

#headeradarea {
  display: none; 
}

#aswift_2 {
  display: none;
}
